mapper.select:
SELECT c.contract_id, v.vendor_name, p.product_name, p.base_price, c.contract_price, c.min_order_qty, c.lead_time, c.contract_status, c.contract_start_date, c.contract_end_date FROM contract c JOIN vendor v ON c.vendor_id = v.vendor_id JOIN product p ON c.product_id = p.product_id JOIN category sc ON p.category_id = sc.category_id JOIN category tc ON sc.top_category_id = tc.category_id WHERE c.is_deleted = 0
AND p.product_name LIKE CONCAT('%', #{request.productName}, '%')
AND tc.category_name LIKE CONCAT('%', #{request.topCategoryName}, '%')
AND sc.category_name LIKE CONCAT('%', #{request.categoryName}, '%')
AND v.vendor_name LIKE CONCAT('%', #{request.vendorName}, '%')
AND v.vendor_type = #{request.vendorType}
AND c.contract_status = #{request.contractStatus}
AND c.contract_start_date = #{request.contractStartDate}
AND c.contract_end_date = #{request.contractEndDate}
AND c.vendor_id = #{vendorId}
ORDER BY c.contract_id DESC LIMIT #{request.size} OFFSET #{request.offset}

SELECT COUNT(1)
FROM contract c
JOIN vendor v ON c.vendor_id = v.vendor_id
JOIN product p ON c.product_id = p.product_id
JOIN category sc ON p.category_id = sc.category_id
JOIN category tc ON sc.top_category_id = tc.category_id
WHERE c.is_deleted = 0

    AND p.product_name LIKE CONCAT('%', #{request.productName}, '%')


    AND tc.category_name LIKE CONCAT('%', #{request.topCategoryName}, '%')


    AND sc.category_name LIKE CONCAT('%', #{request.categoryName}, '%')


    AND v.vendor_name LIKE CONCAT('%', #{request.vendorName}, '%')


    AND v.vendor_type = #{request.vendorType}


    AND c.contract_status = #{request.contractStatus}


    AND c.contract_start_date = #{request.contractStartDate}


    AND c.contract_end_date = #{request.contractEndDate}


    AND c.vendor_id = #{vendorId}
