mapper.select:
SELECT
po.purchase_order_id,
po.purchase_order_code,
po.purchase_order_status AS status,
po.vendor_id,
po.warehouse_id,
po.created_member_id AS requesterId,
po.created_at,
po.total_amount
FROM purchase_order po
WHERE 1 = 1

    AND po.purchase_order_status = #{condition.status}


    AND po.created_member_id = #{condition.requesterId}


    AND po.created_at >= #{condition.startDate}


    AND po.created_at <= #{condition.endDate}


    AND po.vendor_member_id = #{condition.vendorMemberId}

ORDER BY po.created_at DESC
LIMIT #{condition.size}
OFFSET #{condition.offset}
