mapper.select:
SELECT target_date AS date, predicted_value AS value, 'PREDICTED' AS type FROM prediction_result WHERE prediction_type = 'sales' AND period_type = 'WEEKLY'
AND franchise_id = #{franchiseId}
AND target_date BETWEEN #{startDate} AND #{endDate}

SELECT target_date AS date, predicted_value AS value, 'PREDICTED' AS type FROM prediction_result WHERE prediction_type = 'order_quantity' AND period_type = 'WEEKLY'
AND franchise_id = #{franchiseId}
AND target_date BETWEEN #{startDate} AND #{endDate}
