mapper.select:
SELECT DATE(po.created_at) AS date, v.vendor_name AS vendorName, COUNT(DISTINCT po.purchase_order_id) AS totalOrders, SUM(pod.quantity) AS totalQuantity, SUM(pod.total_price) AS totalAmount FROM purchase_order po JOIN purchase_order_detail pod ON po.purchase_order_id = pod.purchase_order_id JOIN vendor v ON po.vendor_id = v.vendor_id WHERE po.purchase_order_status IN ('APPROVED', 'SHIPPED', 'WAREHOUSED') AND DATE(po.created_at) BETWEEN #{startDate} AND #{endDate}
AND po.vendor_id = #{vendorId}
GROUP BY DATE(po.created_at), v.vendor_name ORDER BY date ASC

SELECT DATE(po.created_at) AS date, v.vendor_name AS vendorName, p.product_name AS productName, SUM(pod.quantity) AS quantity, SUM(pod.total_price) AS amount FROM purchase_order po JOIN purchase_order_detail pod ON po.purchase_order_id = pod.purchase_order_id JOIN product p ON pod.product_id = p.product_id JOIN vendor v ON po.vendor_id = v.vendor_id WHERE po.purchase_order_status IN ('APPROVED', 'SHIPPED', 'WAREHOUSED') AND DATE(po.created_at) BETWEEN #{startDate} AND #{endDate}
AND po.vendor_id = #{vendorId}
GROUP BY DATE(po.created_at), v.vendor_name, p.product_name ORDER BY date ASC

SELECT DATE(po.created_at) AS date, SUM(pod.quantity) AS totalQuantity, SUM(pod.total_price) AS totalAmount FROM purchase_order po JOIN purchase_order_detail pod ON po.purchase_order_id = pod.purchase_order_id WHERE po.purchase_order_status IN ('APPROVED', 'SHIPPED', 'WAREHOUSED') AND DATE(po.created_at) BETWEEN #{startDate} AND #{endDate}
AND po.vendor_id = #{vendorId}
GROUP BY DATE(po.created_at) ORDER BY date ASC
