mapper.select:
SELECT DATE(so.created_at) AS date, f.franchise_name AS franchiseName, COUNT(DISTINCT so.store_order_id) AS totalOrders, SUM(sod.quantity) AS totalQuantity, SUM(sod.total_price) AS totalAmount FROM store_order so JOIN store_order_detail sod ON so.store_order_id = sod.store_order_id JOIN franchise f ON so.franchise_id = f.franchise_id WHERE so.store_order_status = 'APPROVED' AND so.created_at BETWEEN #{startDate} AND #{endDate}
AND so.franchise_id = #{franchiseId}
GROUP BY DATE(so.created_at), f.franchise_name ORDER BY date ASC

SELECT DATE(so.created_at) AS date, f.franchise_name AS franchiseName, p.product_name AS productName, SUM(sod.quantity) AS quantity, SUM(sod.total_price) AS amount FROM store_order so JOIN store_order_detail sod ON so.store_order_id = sod.store_order_id JOIN product p ON sod.product_id = p.product_id JOIN franchise f ON so.franchise_id = f.franchise_id WHERE so.store_order_status = 'APPROVED' AND so.created_at BETWEEN #{startDate} AND #{endDate}
AND so.franchise_id = #{franchiseId}
GROUP BY DATE(so.created_at), f.franchise_name, p.product_name ORDER BY date ASC
